import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../modules/auth/AuthContext';
import '../styles/wishlist.css';

export const WishlistPage = () => {
    const { user } = useAuth();
    const navigate = useNavigate();
    const [wishlistItems, setWishlistItems] = useState([]);
    const [notifications, setNotifications] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [showNotifications, setShowNotifications] = useState(false);

    // Mock data de productos
    const products = [
        {
            id: 1,
            name: "Collar artesanal de semillas",
            price: 12000,
            originalPrice: 15000,
            numericPrice: 12000,
            category: "joyeria",
            description: "Hermoso collar hecho con semillas naturales costarricenses",
            seller: "María Artesana",
            sellerId: "seller-001",
            hasDiscount: true,
            discountPercentage: 20,
            priceHistory: [
                { date: '2024-01-10', price: 15000 },
                { date: '2024-01-15', price: 12000 }
            ]
        },
        {
            id: 2,
            name: "Bolso tejido a mano",
            price: 18500,
            originalPrice: 18500,
            numericPrice: 18500,
            category: "textil",
            description: "Bolso tradicional tejido por artesanas locales",
            seller: "Ana Tejidos",
            sellerId: "seller-002",
            hasDiscount: false,
            priceHistory: [
                { date: '2024-01-05', price: 20000 },
                { date: '2024-01-12', price: 18500 }
            ]
        },
        {
            id: 3,
            name: "Vasija de cerámica tradicional",
            price: 13440,
            originalPrice: 15800,
            numericPrice: 13440,
            category: "ceramica",
            description: "Vasija elaborada con técnicas ancestrales de alfarería",
            seller: "Carlos Ceramista",
            sellerId: "seller-003",
            hasDiscount: true,
            discountPercentage: 15,
            priceHistory: [
                { date: '2024-01-08', price: 15800 },
                { date: '2024-01-16', price: 13440 }
            ]
        }
    ];

    useEffect(() => {
        loadWishlist();
        loadNotifications();
    }, [user]);

    const loadWishlist = () => {
        setIsLoading(true);
        try {
            const savedWishlist = localStorage.getItem(`wishlist_${user?.id}`);
            if (savedWishlist) {
                const wishlistIds = JSON.parse(savedWishlist);
                // Obtener detalles completos de los productos
                const wishlistProducts = products.filter(product =>
                    wishlistIds.includes(product.id)
                );
                setWishlistItems(wishlistProducts);
            }
        } catch (error) {
            console.error('Error al cargar wishlist:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const loadNotifications = () => {
        try {
            const savedNotifications = localStorage.getItem(`notifications_${user?.id}`);
            if (savedNotifications) {
                const notifs = JSON.parse(savedNotifications);
                setNotifications(notifs.filter(n => !n.read)); // Solo las no leídas
            } else {
                // Crear algunas notificaciones de ejemplo
                const exampleNotifications = [
                    {
                        id: 'notif-001',
                        type: 'price_drop',
                        productId: 1,
                        productName: 'Collar artesanal de semillas',
                        oldPrice: 15000,
                        newPrice: 12000,
                        discountPercentage: 20,
                        date: new Date().toISOString(),
                        read: false,
                        sellerId: 'seller-001',
                        sellerName: 'María Artesana'
                    },
                    {
                        id: 'notif-002',
                        type: 'discount',
                        productId: 3,
                        productName: 'Vasija de cerámica tradicional',
                        oldPrice: 15800,
                        newPrice: 13440,
                        discountPercentage: 15,
                        date: new Date(Date.now() - 86400000).toISOString(), // Ayer
                        read: false,
                        sellerId: 'seller-003',
                        sellerName: 'Carlos Ceramista'
                    }
                ];
                localStorage.setItem(`notifications_${user?.id}`, JSON.stringify(exampleNotifications));
                setNotifications(exampleNotifications);
            }
        } catch (error) {
            console.error('Error al cargar notificaciones:', error);
        }
    };

    const removeFromWishlist = (productId) => {
        try {
            const updatedItems = wishlistItems.filter(item => item.id !== productId);
            setWishlistItems(updatedItems);

            const wishlistIds = updatedItems.map(item => item.id);
            localStorage.setItem(`wishlist_${user?.id}`, JSON.stringify(wishlistIds));

            // Mostrar confirmación
            showToast('Producto eliminado de tu lista de deseos', 'success');
        } catch (error) {
            console.error('Error al eliminar de wishlist:', error);
            showToast('Error al eliminar el producto', 'error');
        }
    };

    const addToCart = (product) => {
        try {
            const savedCart = localStorage.getItem('artesjac-cart');
            let currentCart = [];

            if (savedCart && savedCart !== 'null') {
                currentCart = JSON.parse(savedCart);
            }

            const existingItemIndex = currentCart.findIndex(item => item.id === product.id);

            if (existingItemIndex > -1) {
                currentCart[existingItemIndex].quantity += 1;
            } else {
                currentCart.push({
                    ...product,
                    price: `₡${product.price.toLocaleString()}`,
                    quantity: 1
                });
            }

            localStorage.setItem('artesjac-cart', JSON.stringify(currentCart));
            showToast(`${product.name} agregado al carrito`, 'success');
        } catch (error) {
            console.error('Error al agregar al carrito:', error);
            showToast('Error al agregar al carrito', 'error');
        }
    };

    const markNotificationAsRead = (notificationId) => {
        try {
            const updatedNotifications = notifications.map(notif =>
                notif.id === notificationId ? { ...notif, read: true } : notif
            );

            // Guardar todas las notificaciones (leídas y no leídas)
            const allNotifications = localStorage.getItem(`notifications_${user?.id}`);
            if (allNotifications) {
                const allNotifs = JSON.parse(allNotifications);
                const updatedAllNotifs = allNotifs.map(notif =>
                    notif.id === notificationId ? { ...notif, read: true } : notif
                );
                localStorage.setItem(`notifications_${user?.id}`, JSON.stringify(updatedAllNotifs));
            }

            // Actualizar estado para mostrar solo las no leídas
            setNotifications(updatedNotifications.filter(n => !n.read));
        } catch (error) {
            console.error('Error al marcar notificación como leída:', error);
        }
    };

    const showToast = (message, type = 'info') => {
        const toast = document.createElement('div');
        toast.className = `toast toast-${type}`;
        toast.textContent = message;
        document.body.appendChild(toast);

        setTimeout(() => {
            toast.classList.add('show');
        }, 100);

        setTimeout(() => {
            toast.classList.remove('show');
            setTimeout(() => {
                document.body.removeChild(toast);
            }, 300);
        }, 3000);
    };

    const getDiscountBadge = (product) => {
        if (product.hasDiscount) {
            return (
                <div className="discount-badge">
                    -{product.discountPercentage}%
                </div>
            );
        }
        return null;
    };

    const getPriceDisplay = (product) => {
        if (product.hasDiscount) {
            return (
                <div className="price-container">
                    <span className="original-price">₡{product.originalPrice.toLocaleString()}</span>
                    <span className="current-price">₡{product.price.toLocaleString()}</span>
                </div>
            );
        }
        return (
            <span className="current-price">₡{product.price.toLocaleString()}</span>
        );
    };

    const formatNotificationDate = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

        if (diffInHours < 1) {
            return 'Hace unos minutos';
        } else if (diffInHours < 24) {
            return `Hace ${diffInHours} hora${diffInHours > 1 ? 's' : ''}`;
        } else {
            const diffInDays = Math.floor(diffInHours / 24);
            return `Hace ${diffInDays} día${diffInDays > 1 ? 's' : ''}`;
        }
    };

    if (isLoading) {
        return (
            <div className="wishlist-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Cargando tu lista de deseos...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="wishlist-container">
            {/* Header */}
            <div className="wishlist-header">
                <div className="header-content">
                    <h1>💖 Mi Lista de Deseos</h1>
                    <p>Productos que te encantan y quieres comprar más tarde</p>
                </div>

                {/* Notificaciones */}
                <div className="notifications-section">
                    <button
                        className={`notifications-btn ${notifications.length > 0 ? 'has-notifications' : ''}`}
                        onClick={() => setShowNotifications(!showNotifications)}
                    >
                        <i className="fa fa-bell"></i>
                        {notifications.length > 0 && (
                            <span className="notification-count">{notifications.length}</span>
                        )}
                    </button>

                    {showNotifications && (
                        <div className="notifications-dropdown">
                            <div className="notifications-header">
                                <h3>🔔 Notificaciones de Precios</h3>
                                <button
                                    className="close-notifications"
                                    onClick={() => setShowNotifications(false)}
                                >
                                    <i className="fa fa-times"></i>
                                </button>
                            </div>

                            <div className="notifications-list">
                                {notifications.length > 0 ? (
                                    notifications.map(notification => (
                                        <div key={notification.id} className="notification-item">
                                            <div className="notification-icon">
                                                {notification.type === 'price_drop' && <i className="fa fa-arrow-down"></i>}
                                                {notification.type === 'discount' && <i className="fa fa-percentage"></i>}
                                            </div>
                                            <div className="notification-content">
                                                <h4>{notification.productName}</h4>
                                                <p>
                                                    {notification.type === 'price_drop' && 'Precio reducido: '}
                                                    {notification.type === 'discount' && `Descuento del ${notification.discountPercentage}%: `}
                                                    <span className="old-price">₡{notification.oldPrice.toLocaleString()}</span>
                                                    {' → '}
                                                    <span className="new-price">₡{notification.newPrice.toLocaleString()}</span>
                                                </p>
                                                <small>Por: {notification.sellerName} • {formatNotificationDate(notification.date)}</small>
                                            </div>
                                            <button
                                                className="mark-read-btn"
                                                onClick={() => markNotificationAsRead(notification.id)}
                                                title="Marcar como leída"
                                            >
                                                <i className="fa fa-check"></i>
                                            </button>
                                        </div>
                                    ))
                                ) : (
                                    <div className="no-notifications">
                                        <i className="fa fa-bell-slash"></i>
                                        <p>No tienes notificaciones nuevas</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}
                </div>
            </div>

            {/* Estadísticas */}
            <div className="wishlist-stats">
                <div className="stat-card">
                    <div className="stat-icon">
                        <i className="fa fa-heart"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{wishlistItems.length}</h3>
                        <p>Productos en Lista</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon">
                        <i className="fa fa-percentage"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{wishlistItems.filter(item => item.hasDiscount).length}</h3>
                        <p>En Descuento</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon">
                        <i className="fa fa-dollar-sign"></i>
                    </div>
                    <div className="stat-content">
                        <h3>₡{wishlistItems.reduce((total, item) => total + item.price, 0).toLocaleString()}</h3>
                        <p>Valor Total</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon">
                        <i className="fa fa-bell"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{notifications.length}</h3>
                        <p>Notificaciones</p>
                    </div>
                </div>
            </div>

            {/* Contenido Principal */}
            <div className="wishlist-content">
                {wishlistItems.length > 0 ? (
                    <>
                        <div className="section-header">
                            <h2>🎨 Tus Productos Favoritos</h2>
                            <div className="actions">
                                <Link to="/shop" className="btn-continue-shopping">
                                    <i className="fa fa-plus"></i> Agregar más productos
                                </Link>
                            </div>
                        </div>

                        <div className="wishlist-grid">
                            {wishlistItems.map(product => (
                                <div key={product.id} className="wishlist-item">
                                    {getDiscountBadge(product)}

                                    <div className="product-image">
                                        <div className="product-image-sim">
                                            <span>Imagen de {product.name}</span>
                                        </div>
                                    </div>

                                    <div className="product-info">
                                        <div className="product-category">
                                            {product.category.charAt(0).toUpperCase() + product.category.slice(1)}
                                        </div>
                                        <h3 className="product-name">{product.name}</h3>
                                        <p className="product-description">{product.description}</p>
                                        <div className="seller-info">
                                            <i className="fa fa-store"></i>
                                            <span>Por: {product.seller}</span>
                                        </div>
                                    </div>

                                    <div className="product-price">
                                        {getPriceDisplay(product)}
                                    </div>

                                    <div className="product-actions">
                                        <button
                                            className="btn-add-to-cart"
                                            onClick={() => addToCart(product)}
                                        >
                                            <i className="fa fa-shopping-cart"></i>
                                            Agregar al Carrito
                                        </button>

                                        <Link
                                            to={`/product/${product.id}`}
                                            className="btn-view-product"
                                        >
                                            <i className="fa fa-eye"></i>
                                            Ver Producto
                                        </Link>

                                        <button
                                            className="btn-remove"
                                            onClick={() => removeFromWishlist(product.id)}
                                            title="Eliminar de lista de deseos"
                                        >
                                            <i className="fa fa-trash"></i>
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </>
                ) : (
                    <div className="empty-wishlist">
                        <div className="empty-icon">
                            <i className="fa fa-heart-broken"></i>
                        </div>
                        <h2>Tu lista de deseos está vacía</h2>
                        <p>Explora nuestra tienda y agrega productos que te gusten para comprarlos más tarde</p>
                        <Link to="/shop" className="btn-start-shopping">
                            <i className="fa fa-shopping-bag"></i>
                            Comenzar a Explorar
                        </Link>
                    </div>
                )}
            </div>

            {/* Consejos */}
            {wishlistItems.length > 0 && (
                <div className="wishlist-tips">
                    <h3>💡 Consejos para tu Lista de Deseos</h3>
                    <div className="tips-grid">
                        <div className="tip-item">
                            <i className="fa fa-bell"></i>
                            <div>
                                <h4>Recibe Notificaciones</h4>
                                <p>Te avisamos cuando bajan los precios de tus productos favoritos.</p>
                            </div>
                        </div>
                        <div className="tip-item">
                            <i className="fa fa-percentage"></i>
                            <div>
                                <h4>Aprovecha Descuentos</h4>
                                <p>Los productos en descuento aparecen marcados con el porcentaje de ahorro.</p>
                            </div>
                        </div>
                        <div className="tip-item">
                            <i className="fa fa-heart"></i>
                            <div>
                                <h4>Comparte tu Lista</h4>
                                <p>Próximamente podrás compartir tu lista de deseos con amigos y familia.</p>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};
