// src/pages/ShopPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { Link } from 'react-router-dom';
import '../styles/shop.css';
import { ShopAPI } from '../api/shop.service';
import { resolveImgUrl } from "../utils/resolveImgUrl";

// Mock de respaldo si la API falla
const mockProducts = [
    { id: 1, name: "Collar artesanal de semillas", price: "‚Ç°12.000", numericPrice: 12000, category: "joyeria", description: "Hermoso collar hecho con semillas naturales costarricenses", images: ["/uploads/sample1.jpg"] },
    { id: 2, name: "Bolso tejido a mano", price: "‚Ç°18.500", numericPrice: 18500, category: "textil", description: "Bolso tradicional tejido por artesanas locales", images: ["/uploads/sample2.jpg"] },
    // ... deja el resto igual si quer√©s
];

const PLACEHOLDER = "/img/product-placeholder.png";

export const ShopPage = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('todos');
    const [searchTerm, setSearchTerm] = useState('');
    const [cartItems, setCartItems] = useState([]);
    const [addingToCart, setAddingToCart] = useState(null);
    const [error, setError] = useState('');

    // üëâ Para cortar el ‚Äútitileo‚Äù: marcamos cu√°les im√°genes ya cargaron
    const [loadedImg, setLoadedImg] = useState({}); // { [productId]: true }

    // Cargar carrito
    useEffect(() => {
        try {
            const savedCart = localStorage.getItem('artesjac-cart');
            if (savedCart && savedCart !== 'null' && savedCart !== '[]') {
                setCartItems(JSON.parse(savedCart));
            }
        } catch (e) {
            console.error('Error al cargar carrito:', e);
        }
    }, []);

    // Cargar productos
    useEffect(() => {
        (async () => {
            setError('');
            try {
                const res = await ShopAPI.list({ limit: 200 });
                const raw = res?.data?.data || [];

                const list = raw.map(p => ({
                    id: p.id || p._id,
                    name: p.name,
                    category: p.category,
                    description: p.description || '',
                    numericPrice: Number(p.price) || 0,
                    price: `‚Ç°${Number(p.price || 0).toLocaleString()}`,
                    // üëá normalizamos im√°genes (si vienen con backslashes, las pasamos a /)
                    images: Array.isArray(p.images)
                        ? p.images.map(s => (s ? String(s).replace(/\\/g, "/") : s))
                        : (p.images ? [String(p.images).replace(/\\/g, "/")] : []),
                }));

                setProducts(list);
            } catch (e) {
                console.error(e);
                setError('No se pudieron cargar los productos. Mostrando datos de ejemplo.');
                setProducts(mockProducts);
            }
        })();
    }, []);

    // Filtrado
    useEffect(() => {
        let filtered = products;

        if (selectedCategory !== 'todos') {
            filtered = filtered.filter(product => product.category === selectedCategory);
        }

        if (searchTerm) {
            filtered = filtered.filter(product =>
                product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                (product.description || '').toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        setFilteredProducts(filtered);
    }, [selectedCategory, searchTerm, products]);

    // Carrito
    const addToCart = async (product) => {
        setAddingToCart(product.id);
        try {
            const savedCart = localStorage.getItem('artesjac-cart');
            let currentCart = [];
            if (savedCart && savedCart !== 'null') currentCart = JSON.parse(savedCart);

            const idx = currentCart.findIndex(item => item.id === product.id);
            if (idx > -1) currentCart[idx].quantity += 1;
            else currentCart.push({ ...product, quantity: 1 });

            localStorage.setItem('artesjac-cart', JSON.stringify(currentCart));
            setCartItems(currentCart);

            await new Promise(resolve => setTimeout(resolve, 500));

            const notification = document.createElement('div');
            notification.style.cssText = `
        position: fixed; top: 20px; right: 20px;
        background: linear-gradient(135deg, #4caf50, #45a049);
        color: white; padding: 1rem 1.5rem; border-radius: 8px;
        box-shadow: 0 4px 15px rgba(76, 175, 80, 0.3);
        z-index: 1000; font-weight: bold; animation: slideIn 0.3s ease;
      `;
            notification.innerHTML = `‚úÖ ${product.name} agregado al carrito!`;
            document.body.appendChild(notification);
            setTimeout(() => notification.remove(), 3000);
        } catch (error) {
            console.error('Error al agregar al carrito:', error);
            alert('Error al agregar el producto al carrito');
        } finally {
            setAddingToCart(null);
        }
    };

    const calculateCartTotal = () =>
        cartItems.reduce((total, item) => total + (item.numericPrice * item.quantity), 0);

    const getTotalCartItems = () =>
        cartItems.reduce((total, item) => total + item.quantity, 0);

    // Categor√≠as
    const categories = useMemo(() => {
        const counts = { textil: 0, joyeria: 0, pintura: 0, ceramica: 0, escultura: 0 };
        products.forEach(p => { if (counts.hasOwnProperty(p.category)) counts[p.category] += 1; });
        return [
            { id: 'todos', name: 'Todos los productos', count: products.length, icon: 'üé®' },
            { id: 'textil', name: 'Textil', count: counts.textil, icon: 'üßµ' },
            { id: 'joyeria', name: 'Joyer√≠a', count: counts.joyeria, icon: 'üíé' },
            { id: 'pintura', name: 'Pintura', count: counts.pintura, icon: 'üé®' },
            { id: 'ceramica', name: 'Cer√°mica', count: counts.ceramica, icon: 'üè∫' },
            { id: 'escultura', name: 'Escultura', count: counts.escultura, icon: 'üóø' },
        ];
    }, [products]);

    // üëâ Obtener primera imagen segura (normalizada y resuelta)
    const resolveFirstImage = (p) => {
        const first = Array.isArray(p.images) ? p.images.find(Boolean) : p.images;
        if (!first) return PLACEHOLDER;
        const normalized = String(first).replace(/\\/g, "/"); // por si viene con '\'
        return resolveImgUrl(normalized);
    };

    // üëâ onError de imagen: setea placeholder SOLO una vez (evita loop/flicker)
    const handleImgError = (e) => {
        const img = e.currentTarget;
        if (img.dataset.fallback === "1") return; // ya se puso placeholder
        img.dataset.fallback = "1";
        img.src = PLACEHOLDER;
    };

    // üëâ onLoad: marcamos como cargada para cortar animaci√≥n del skeleton
    const handleImgLoad = (productId) => {
        setLoadedImg(prev => ({ ...prev, [productId]: true }));
    };

    return (
        <main className="shop-container">
            {/* Header */}
            <div className="shop-header">
                <h1>Tienda ArtesJAC</h1>
                <p>Descubre arte aut√©ntico costarricense hecho a mano por talentosos artesanos locales</p>
            </div>

            {/* Carrito */}
            <div className="cart-indicator">
                <div className="cart-indicator-info">
                    <i className="fa fa-shopping-cart"></i>
                    <span>
                        {getTotalCartItems()} producto{getTotalCartItems() !== 1 ? 's' : ''} en el carrito -
                        Total: ‚Ç°{calculateCartTotal().toLocaleString()}
                    </span>
                </div>
                <Link to="/cart" className="view-cart-btn">
                    <i className="fa fa-arrow-right"></i> Ver Carrito
                </Link>
            </div>

            {/* Buscador */}
            <div className="shop-search">
                <input
                    type="text"
                    placeholder="üîç Buscar productos, categor√≠as..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            <div className="shop-content">
                {/* Sidebar */}
                <aside className="shop-sidebar">
                    <h3>üìÇ Categor√≠as</h3>
                    <div className="category-filters">
                        {categories.map(category => (
                            <button
                                key={category.id}
                                className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
                                onClick={() => setSelectedCategory(category.id)}
                            >
                                <span style={{ marginRight: '0.5rem' }}>{category.icon}</span>
                                {category.name} ({category.count})
                            </button>
                        ))}
                    </div>
                </aside>

                {/* Grid */}
                <section className="products-section">
                    <div className="products-header">
                        <h2>
                            {selectedCategory === 'todos'
                                ? 'üé® Todos los productos'
                                : `${categories.find(c => c.id === selectedCategory)?.icon || 'üé®'} ${categories.find(c => c.id === selectedCategory)?.name || ''}`}
                        </h2>
                        <span className="products-count">{filteredProducts.length} productos</span>
                    </div>

                    {error && (
                        <div className="error-message" style={{ marginBottom: 16 }}>
                            <i className="fa fa-exclamation-triangle"></i>
                            <span>{error}</span>
                        </div>
                    )}

                    <div className="products-grid">
                        {filteredProducts.map(product => {
                            const imgLoaded = !!loadedImg[product.id];
                            const src = resolveFirstImage(product);
                            return (
                                <div key={product.id} className="product-card">
                                    <div className="product-image">
                                        <div
                                            className="product-image-sim"
                                            // üîá Cuando carga la imagen, apagamos la animaci√≥n del contenedor
                                            style={{
                                                animation: imgLoaded ? 'none' : undefined,
                                                background: imgLoaded ? 'transparent' : undefined
                                            }}
                                        >
                                            <img
                                                src={src}
                                                alt={`Imagen de ${product.name}`}
                                                className="product-thumb"
                                                style={{ width: '100%', height: '100%', objectFit: 'cover', display: 'block' }}
                                                onLoad={() => handleImgLoad(product.id)}
                                                onError={handleImgError}
                                                loading="lazy"
                                                decoding="async"
                                                crossOrigin="anonymous"
                                                data-fallback="0"
                                            />
                                        </div>
                                    </div>

                                    <div className="product-info">
                                        <h3 className="product-name">{product.name}</h3>
                                        <p className="product-price">{product.price}</p>
                                        <p className="product-category">
                                            {(categories.find(c => c.id === product.category)?.icon || 'üé®')}{' '}
                                            {product.category ? product.category.charAt(0).toUpperCase() + product.category.slice(1) : ''}
                                        </p>
                                    </div>

                                    <div className="product-actions">
                                        <button
                                            className="btn-add-to-cart"
                                            onClick={() => addToCart(product)}
                                            disabled={addingToCart === product.id}
                                        >
                                            {addingToCart === product.id ? (
                                                <>
                                                    <i className="fa fa-spinner fa-spin"></i>
                                                    Agregando...
                                                </>
                                            ) : (
                                                <>
                                                    <i className="fa fa-shopping-cart"></i>
                                                    Agregar al carrito
                                                </>
                                            )}
                                        </button>

                                        <Link
                                            to={`/product/${product.id}`}
                                            className="btn-view-details"
                                            style={{ textDecoration: 'none' }}
                                        >
                                            <i className="fa fa-eye"></i>
                                            Ver detalles
                                        </Link>
                                    </div>
                                </div>
                            );
                        })}
                    </div>

                    {filteredProducts.length === 0 && (
                        <div className="no-products">
                            <i className="fa fa-search" style={{ fontSize: '3rem', color: '#666', marginBottom: '1rem' }}></i>
                            <h3>No se encontraron productos</h3>
                            <p>
                                {searchTerm ?
                                    `No hay productos que coincidan con "${searchTerm}"` :
                                    'No hay productos en esta categor√≠a'
                                }
                            </p>
                            <p>Intenta cambiar los filtros o el t√©rmino de b√∫squeda</p>
                        </div>
                    )}
                </section>
            </div>

            {/* Animaci√≥n de notificaci√≥n */}
            <style jsx>{`
        @keyframes slideIn {
          from { transform: translateX(100%); opacity: 0; }
          to   { transform: translateX(0);     opacity: 1; }
        }
      `}</style>
        </main>
    );
};
