// src/pages/ProductPage.js
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { ShopAPI } from "../api/shop.service";
import "../styles/product.css";
import noImage from "../assets/images/noimage.png";
import { resolveImgUrl } from "../utils/resolveImgUrl";

const fmtCRC = (n) => typeof n === "number" ? `₡${n.toLocaleString("es-CR")}` : n;

export const ProductPage = () => {
    const { slug } = useParams();
    const [product, setProduct] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [avg, setAvg] = useState(0);
    const [count, setCount] = useState(0);
    const [loading, setLoading] = useState(true);

    const firstImg = (p) => {
        const arr = Array.isArray(p?.images) ? p.images : [];
        const candidate = arr.find(Boolean);
        return candidate ? String(candidate).replace(/\\/g, "/") : null;
    };

    const load = async () => {
        setLoading(true);
        try {
            const detail = await ShopAPI.detail(slug);
            if (!detail?.ok) throw new Error("No encontrado");
            const data = detail.data;
            setProduct(data);
            setAvg(data.averageRating || 0);
            setCount(data.reviewsCount || 0);

            // Cargar reseñas aprobadas públicas
            const r = await ShopAPI.reviews(data._id, { page: 1, limit: 10 });
            setReviews(Array.isArray(r?.data) ? r.data : []);
        } catch (e) {
            console.error("Product detail error:", e?.response?.data || e.message);
            setProduct(null);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => { load(); }, [slug]);

    if (loading) return <div className="product-page"><p>Cargando…</p></div>;
    if (!product) return <div className="product-page"><p>Producto no encontrado</p></div>;

    const img = firstImg(product);

    return (
        <div className="product-page">
            <div className="product-hero">
                <div className="image">
                    <img
                        src={img ? resolveImgUrl(img) : noImage}
                        alt={product.title}
                        onError={(e) => { e.currentTarget.src = noImage; }}
                    />
                </div>

                <div className="info">
                    <h1>{product.title}</h1>
                    <div className="price">{fmtCRC(product.price)}</div>
                    {product.stock <= 0 ? (
                        <span className="badge badge-out">Sin stock</span>
                    ) : (
                        <span className="badge badge-in">Disponible</span>
                    )}

                    {product.vendorId && (
                        <div className="seller">
                            <h4>Vendido por</h4>
                            <div className="seller-line">
                                {product.vendorId.avatar && (
                                    <img className="seller-avatar" src={product.vendorId.avatar} alt={product.vendorId.name} />
                                )}
                                <div>
                                    <div className="seller-name">{product.vendorId.companyName || product.vendorId.name}</div>
                                    <div className="seller-role">{String(product.vendorId.role || "").toUpperCase()}</div>
                                </div>
                            </div>
                        </div>
                    )}

                    <div className="rating">
                        <strong>Valoración:</strong> {avg} ⭐ ({count} reseña{count === 1 ? "" : "s"})
                    </div>

                    <div className="actions">
                        <button disabled={product.stock <= 0}>Agregar al carrito</button>
                        <Link to="/shop" className="link-back">← Volver a la tienda</Link>
                    </div>
                </div>
            </div>

            <div className="product-details">
                <h3>Descripción</h3>
                <p>{product.description || "Sin descripción"}</p>
            </div>

            <div className="product-reviews">
                <h3>Reseñas</h3>
                {reviews.length === 0 ? (
                    <p>Este producto aún no tiene reseñas.</p>
                ) : (
                    <ul className="review-list">
                        {reviews.map((rv) => (
                            <li key={rv._id} className="review-item">
                                <div className="review-header">
                                    {rv.userId?.avatar && <img src={rv.userId.avatar} alt={rv.userId?.name} className="review-avatar" />}
                                    <div>
                                        <div className="review-name">{rv.userId?.name || "Usuario"}</div>
                                        <div className="review-stars">{"⭐".repeat(rv.rating)}</div>
                                    </div>
                                </div>
                                {rv.comment && <p className="review-comment">{rv.comment}</p>}
                                <div className="review-date">{new Date(rv.createdAt).toLocaleDateString("es-CR")}</div>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default ProductPage;
