import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { WishlistButton } from './WishlistButton';
import '../styles/product.css';

export const ProductPage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [addedToCart, setAddedToCart] = useState(false);

    // Mock data de productos con información de descuentos
    const products = [
        {
            id: 1,
            name: 'Collar artesanal de semillas',
            price: '₡12.000',
            numericPrice: 12000,
            originalPrice: 15000,
            category: 'joyeria',
            description: 'Hermoso collar hecho a mano con semillas naturales de Costa Rica. Una pieza única que refleja la tradición artesanal costarricense.',
            seller: 'María Artesana',
            sellerId: 'seller-001',
            hasDiscount: true,
            discountPercentage: 20
        },
        {
            id: 2,
            name: 'Bolso tejido a mano',
            price: '₡18.500',
            numericPrice: 18500,
            originalPrice: 18500,
            category: 'textil',
            description: 'Bolso artesanal tejido con técnicas tradicionales, perfecto para el uso diario y con un toque de elegancia costarricense.',
            seller: 'Ana Tejidos',
            sellerId: 'seller-002',
            hasDiscount: false
        },
        {
            id: 3,
            name: 'Cuadro colorido abstracto',
            price: '₡22.000',
            numericPrice: 22000,
            originalPrice: 22000,
            category: 'pintura',
            description: 'Obra de arte original que captura los colores vibrantes de Costa Rica en un estilo abstracto contemporáneo.',
            seller: 'Carlos Pintor',
            sellerId: 'seller-003',
            hasDiscount: false
        },
        {
            id: 4,
            name: 'Vasija de cerámica tradicional',
            price: '₡13.440',
            numericPrice: 13440,
            originalPrice: 15800,
            category: 'ceramica',
            description: 'Vasija decorativa elaborada con técnicas de cerámica tradicional, ideal para decorar cualquier espacio del hogar.',
            seller: 'Pedro Ceramista',
            sellerId: 'seller-004',
            hasDiscount: true,
            discountPercentage: 15
        },
        {
            id: 5,
            name: 'Aretes de madera tallada',
            price: '₡8.500',
            numericPrice: 8500,
            originalPrice: 8500,
            category: 'joyeria',
            description: 'Elegantes aretes tallados en madera nacional, ligeros y cómodos para uso diario.',
            seller: 'Luisa Madera',
            sellerId: 'seller-005',
            hasDiscount: false
        },
        {
            id: 6,
            name: 'Tapete tejido multicolor',
            price: '₡21.250',
            numericPrice: 21250,
            originalPrice: 25000,
            category: 'textil',
            description: 'Tapete artesanal con diseños geométricos en múltiples colores, perfecto para dar vida a cualquier habitación.',
            seller: 'Rosa Textiles',
            sellerId: 'seller-006',
            hasDiscount: true,
            discountPercentage: 15
        },
        {
            id: 7,
            name: 'Retrato al óleo',
            price: '₡35.000',
            numericPrice: 35000,
            originalPrice: 35000,
            category: 'pintura',
            description: 'Retrato realista pintado al óleo, una obra de arte que captura la esencia y belleza del modelo.',
            seller: 'Arturo Pincel',
            sellerId: 'seller-007',
            hasDiscount: false
        },
        {
            id: 8,
            name: 'Juego de tazas de arcilla',
            price: '₡11.900',
            numericPrice: 11900,
            originalPrice: 14000,
            category: 'ceramica',
            description: 'Set de 4 tazas artesanales de arcilla, perfectas para disfrutar de bebidas calientes con estilo tradicional.',
            seller: 'Elena Barro',
            sellerId: 'seller-008',
            hasDiscount: true,
            discountPercentage: 15
        },
        {
            id: 9,
            name: 'Pulsera de cuentas naturales',
            price: '₡9.800',
            numericPrice: 9800,
            originalPrice: 9800,
            category: 'joyeria',
            description: 'Pulsera elegante con cuentas de materiales naturales, cómoda y versátil para cualquier ocasión.',
            seller: 'Carmen Joyas',
            sellerId: 'seller-009',
            hasDiscount: false
        },
        {
            id: 10,
            name: 'Manta tejida artesanal',
            price: '₡27.200',
            numericPrice: 27200,
            originalPrice: 32000,
            category: 'textil',
            description: 'Manta suave y cálida tejida a mano con lana natural, perfecta para noches frescas.',
            seller: 'Sofia Lana',
            sellerId: 'seller-010',
            hasDiscount: true,
            discountPercentage: 15
        },
        {
            id: 11,
            name: 'Paisaje costarricense',
            price: '₡28.000',
            numericPrice: 28000,
            originalPrice: 28000,
            category: 'pintura',
            description: 'Hermosa pintura que retrata un paisaje típico costarricense, lleno de verdor y vida natural.',
            seller: 'Diego Natura',
            sellerId: 'seller-011',
            hasDiscount: false
        },
        {
            id: 12,
            name: 'Platos decorativos de barro',
            price: '₡16.575',
            numericPrice: 16575,
            originalPrice: 19500,
            category: 'ceramica',
            description: 'Set de platos decorativos hechos de barro natural, ideales para decoración o uso ceremonial.',
            seller: 'Miguel Tierra',
            sellerId: 'seller-012',
            hasDiscount: true,
            discountPercentage: 15
        }
    ];

    useEffect(() => {
        const foundProduct = products.find(p => p.id === parseInt(id));
        setProduct(foundProduct);
    }, [id]);

    const handleAddToCart = () => {
        if (!product) return;

        try {
            const savedCart = localStorage.getItem('artesjac-cart');
            let cart = savedCart ? JSON.parse(savedCart) : [];

            const existingItem = cart.find(item => item.id === product.id);

            if (existingItem) {
                cart = cart.map(item =>
                    item.id === product.id
                        ? { ...item, quantity: item.quantity + quantity }
                        : item
                );
            } else {
                cart.push({ ...product, quantity });
            }

            localStorage.setItem('artesjac-cart', JSON.stringify(cart));
            setAddedToCart(true);
            setTimeout(() => setAddedToCart(false), 3000);

        } catch (error) {
            console.error('Error al agregar al carrito:', error);
        }
    };

    const getPriceDisplay = () => {
        if (!product) return null;

        if (product.hasDiscount) {
            return (
                <div className="price-display-container">
                    <div className="discount-info">
                        <span className="discount-badge">-{product.discountPercentage}% OFF</span>
                        <span className="savings-text">¡Ahorras ₡{(product.originalPrice - product.numericPrice).toLocaleString()}!</span>
                    </div>
                    <div className="price-container">
                        <span className="original-price">₡{product.originalPrice.toLocaleString()}</span>
                        <span className="current-price">{product.price}</span>
                    </div>
                </div>
            );
        }

        return (
            <div className="price-display-container">
                <div className="price-container">
                    <span className="current-price">{product.price}</span>
                </div>
            </div>
        );
    };

    if (!product) {
        return (
            <main className="product-container">
                <div className="product-not-found">
                    <h2>Producto no encontrado</h2>
                    <p>El producto que buscas no existe o ha sido removido.</p>
                    <Link to="/shop" className="btn-back-shop">
                        Volver a la tienda
                    </Link>
                </div>
            </main>
        );
    }

    return (
        <main className="product-container">
            {/* Breadcrumb */}
            <nav className="breadcrumb">
                <Link to="/">Inicio</Link>
                <span>/</span>
                <Link to="/shop">Tienda</Link>
                <span>/</span>
                <span className="current">{product.name}</span>
            </nav>

            <div className="product-detail">
                {/* Imagen del producto */}
                <div className="product-image-section">
                    <div className="product-image-main">
                        <div className="product-image-sim large">
                            {product.hasDiscount && (
                                <div className="image-discount-overlay">
                                    <span className="discount-percentage">-{product.discountPercentage}%</span>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Información del producto */}
                <div className="product-info-section">
                    <div className="product-category">
                        {product.category.charAt(0).toUpperCase() + product.category.slice(1)}
                    </div>

                    <h1 className="product-title">{product.name}</h1>

                    <div className="seller-info">
                        <i className="fa fa-store"></i>
                        <span>Vendido por: <strong>{product.seller}</strong></span>
                    </div>

                    <div className="product-price-main">
                        {getPriceDisplay()}
                    </div>

                    <div className="product-description">
                        <h3>Descripción</h3>
                        <p>{product.description}</p>
                    </div>

                    <div className="product-purchase">
                        <div className="quantity-section">
                            <label>Cantidad:</label>
                            <div className="quantity-controls">
                                <button
                                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                                    className="quantity-btn"
                                >
                                    <i className="fa fa-minus"></i>
                                </button>
                                <span className="quantity-display">{quantity}</span>
                                <button
                                    onClick={() => setQuantity(quantity + 1)}
                                    className="quantity-btn"
                                >
                                    <i className="fa fa-plus"></i>
                                </button>
                            </div>
                        </div>

                        <div className="purchase-actions">
                            <button
                                className={`btn-add-to-cart ${addedToCart ? 'added' : ''}`}
                                onClick={handleAddToCart}
                            >
                                {addedToCart ? (
                                    <>
                                        <i className="fa fa-check"></i> ¡Agregado al carrito!
                                    </>
                                ) : (
                                    <>
                                        <i className="fa fa-shopping-cart"></i> Agregar al carrito
                                    </>
                                )}
                            </button>

                            {/* Botón de Wishlist */}
                            <WishlistButton
                                product={product}
                                size="large"
                                className="product-wishlist-btn"
                            />

                            <button
                                className="btn-buy-now"
                                onClick={() => {
                                    handleAddToCart();
                                    setTimeout(() => navigate('/cart'), 500);
                                }}
                            >
                                <i className="fa fa-bolt"></i> Comprar ahora
                            </button>
                        </div>
                    </div>

                    <div className="product-features">
                        <div className="feature">
                            <i className="fa fa-truck"></i>
                            <span>Envío gratis a todo Costa Rica</span>
                        </div>
                        <div className="feature">
                            <i className="fa fa-shield-alt"></i>
                            <span>Garantía de calidad artesanal</span>
                        </div>
                        <div className="feature">
                            <i className="fa fa-heart"></i>
                            <span>Hecho con amor por artesanos locales</span>
                        </div>
                        {product.hasDiscount && (
                            <div className="feature discount-feature">
                                <i className="fa fa-percentage"></i>
                                <span>¡Oferta especial por tiempo limitado!</span>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Botón de regreso */}
            <div className="navigation-actions">
                <Link to="/shop" className="btn-back">
                    <i className="fa fa-arrow-left"></i> Volver a la tienda
                </Link>
            </div>

            {/* Estilos adicionales para los nuevos elementos */}
            <style jsx>{`
                .price-display-container {
                    text-align: center;
                    margin: 1.5rem 0;
                }

                .discount-info {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 1rem;
                    margin-bottom: 1rem;
                    flex-wrap: wrap;
                }

                .discount-badge {
                    background: linear-gradient(135deg, #f44336, #d32f2f);
                    color: white;
                    padding: 0.5rem 1rem;
                    border-radius: 20px;
                    font-weight: bold;
                    font-size: 1rem;
                    box-shadow: 0 2px 8px rgba(244, 67, 54, 0.3);
                    animation: pulse 2s infinite;
                }

                .savings-text {
                    background: linear-gradient(135deg, #4caf50, #45a049);
                    color: white;
                    padding: 0.4rem 0.8rem;
                    border-radius: 15px;
                    font-size: 0.9rem;
                    font-weight: bold;
                }

                .price-container {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 1rem;
                    flex-wrap: wrap;
                }

                .original-price {
                    color: #999;
                    text-decoration: line-through;
                    font-size: 1.2rem;
                }

                .current-price {
                    color: #ff5722;
                    font-size: 2rem;
                    font-weight: bold;
                }

                .seller-info {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    color: #ccc;
                    margin: 1rem 0;
                    padding: 0.8rem;
                    background-color: #2b2b2b;
                    border-radius: 8px;
                }

                .seller-info i {
                    color: #ff5722;
                }

                .seller-info strong {
                    color: #fff;
                }

                .image-discount-overlay {
                    position: absolute;
                    top: 1rem;
                    right: 1rem;
                    background: linear-gradient(135deg, #f44336, #d32f2f);
                    color: white;
                    padding: 0.5rem 0.8rem;
                    border-radius: 20px;
                    font-weight: bold;
                    box-shadow: 0 2px 8px rgba(244, 67, 54, 0.3);
                    z-index: 10;
                }

                .discount-percentage {
                    font-size: 1.1rem;
                }

                .discount-feature {
                    border-left: 4px solid #f44336;
                    background-color: rgba(244, 67, 54, 0.1);
                    padding-left: 1rem;
                }

                .discount-feature i {
                    color: #f44336;
                }

                .product-wishlist-btn {
                    margin: 0.5rem 0;
                }

                @keyframes pulse {
                    0%, 100% {
                        transform: scale(1);
                    }
                    50% {
                        transform: scale(1.05);
                    }
                }

                @media (max-width: 768px) {
                    .discount-info {
                        flex-direction: column;
                        gap: 0.5rem;
                    }

                    .price-container {
                        flex-direction: column;
                        gap: 0.5rem;
                    }

                    .current-price {
                        font-size: 1.6rem;
                    }
                }
            `}</style>
        </main>
    );
};
