// src/api/products.service.js
import { api } from "../api";

export const ProductsAPI = {
    mine: (params = {}) => api.get("/products/me", { params }),                  // GET /api/products/me
    listByVendor: (vendorId, params = {}) => api.get(`/products/by-vendor/${vendorId}`, { params }),
    get: (idOrSlug) => api.get(`/products/${idOrSlug}`),                         // GET /api/products/:idOrSlug
    create: (payload) => api.post("/products", payload),                         // POST /api/products
    update: (id, payload) => api.put(`/products/${id}`, payload),                // PUT /api/products/:id
    remove: (id) => api.delete(`/products/${id}`),                               // DELETE /api/products/:id
};
