// src/api/shop.service.js
import { api } from "../api";

/** Endpoints públicos de TIENDA (no requieren token) */
export const ShopAPI = {
    // Lista de productos activos con filtros/paginación:
    // params: { q, category, sort, page, limit }
    list: (params = {}) =>
        api.get("/shop/products", { params }).then((r) => r.data),

    // Detalle por slug (recomendado):
    detail: (slug) =>
        api.get(`/shop/products/${slug}`).then((r) => r.data),

    // Alias si en algún lado llamaste get(slugOrId)
    get: (slugOrId) =>
        api.get(`/shop/products/${slugOrId}`).then((r) => r.data),

    // Reseñas públicas aprobadas
    // params extra: { page, limit }
    reviews: (productId, params = {}) =>
        api
            .get(`/shop/reviews`, { params: { product: productId, ...params } })
            .then((r) => r.data),
};
