// src/pages/CartPage.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../modules/auth/AuthContext";
import { CartAPI } from "../api/cart.service";
import "../styles/cart.css";

export const CartPage = () => {
    const { user, token } = useAuth() || {};
    const authToken =
        token || localStorage.getItem("token") || localStorage.getItem("authToken");
    const navigate = useNavigate();

    const [cartItems, setCartItems] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    // Mapea el item del backend (con populate) a la forma que espera tu UI
    const mapServerItems = (serverItems = []) =>
        serverItems.map((it) => {
            const p = it.productId; // puede ser ObjectId o documento populado
            const id =
                typeof p === "object" && p
                    ? (p._id || p.id || it.productId)
                    : it.productId;

            const title =
                (typeof p === "object" && p
                    ? p.title || p.name
                    : it.title) || "Producto";

            // priorizamos snapshot guardado en carrito (priceAtAdd) y caemos al precio actual del producto
            const numericPrice =
                typeof it.priceAtAdd === "number"
                    ? it.priceAtAdd
                    : typeof p === "object" && typeof p?.price === "number"
                        ? p.price
                        : 0;

            const category =
                (typeof p === "object" && (p._normalizedCategory || p.category)) || "";

            // imagen (primera disponible)
            let image = "";
            if (typeof p === "object" && p) {
                if (Array.isArray(p.images) && p.images.length) {
                    image = String(p.images.find(Boolean) || "").replace(/\\/g, "/");
                } else if (p.image || p.img || p.thumbnail) {
                    image = String(p.image || p.img || p.thumbnail).replace(/\\/g, "/");
                }
            }

            return {
                id,                   // ðŸ‘ˆ tu UI usa "id" en los handlers y Links
                name: title,          // ðŸ‘ˆ tu UI muestra "name"
                title,
                quantity: Number(it.quantity || 0),
                numericPrice,         // ðŸ‘ˆ usado para cÃ¡lculos en tu UI
                price: numericPrice,
                category,
                image,
            };
        });

    const loadCartItems = async () => {
        setIsLoading(true);
        try {
            if (authToken) {
                const r = await CartAPI.mine(authToken);
                const items = mapServerItems(r?.data?.data?.items || []);
                setCartItems(items);
            } else {
                // Invitado: sigue funcionando con localStorage
                const saved = localStorage.getItem("artesjac-cart");
                const items = saved && saved !== "null" ? JSON.parse(saved) : [];
                setCartItems(items);
            }
        } catch (e) {
            console.error("Error al cargar carrito:", e?.response?.data || e.message);
            setCartItems([]);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        loadCartItems();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const updateQuantity = async (productId, newQuantity) => {
        if (newQuantity < 1) return removeItem(productId);
        try {
            if (authToken) {
                await CartAPI.update({ productId, quantity: newQuantity }, authToken);
                await loadCartItems();
            } else {
                const updated = cartItems.map((it) =>
                    it.id === productId ? { ...it, quantity: newQuantity } : it
                );
                setCartItems(updated);
                localStorage.setItem("artesjac-cart", JSON.stringify(updated));
            }
        } catch (e) {
            alert("No se pudo actualizar la cantidad");
        }
    };

    const removeItem = async (productId) => {
        try {
            if (authToken) {
                await CartAPI.remove(productId, authToken);
                await loadCartItems();
            } else {
                const updated = cartItems.filter((it) => it.id !== productId);
                setCartItems(updated);
                localStorage.setItem("artesjac-cart", JSON.stringify(updated));
            }
        } catch (e) {
            alert("No se pudo eliminar el producto");
        }
    };

    const clearCart = async () => {
        if (!window.confirm("Â¿EstÃ¡s seguro de que deseas vaciar el carrito?")) return;
        try {
            if (authToken) {
                await CartAPI.clear(authToken);
                await loadCartItems();
            } else {
                setCartItems([]);
                localStorage.setItem("artesjac-cart", JSON.stringify([]));
            }
        } catch (e) {
            alert("No se pudo vaciar el carrito");
        }
    };

    const calculateSubtotal = () =>
        cartItems.reduce(
            (acc, it) =>
                acc + Number(it.numericPrice || 0) * Number(it.quantity || 0),
            0
        );

    const calculateShipping = () => (calculateSubtotal() >= 50000 ? 0 : 3500);
    const calculateTotal = () => calculateSubtotal() + calculateShipping();

    const handleCheckout = () => {
        if (cartItems.length === 0) return alert("Tu carrito estÃ¡ vacÃ­o");
        navigate("/checkout");
    };

    if (isLoading) {
        return (
            <div className="cart-container">
                <div className="loading-container">
                    <div className="loading-spinner" />
                    <p>Cargando carrito...</p>
                </div>
            </div>
        );
    }

