// src/pages/ProductPage.js
import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../modules/auth/AuthContext";
import { ShopAPI } from "../api/shop.service";
import { ReviewsAPI } from "../api/reviews.service";
import { CartAPI } from "../api/cart.service";
import { resolveImgUrl } from "../utils/resolveImgUrl";
import noImage from "../assets/images/noimage.png";
import "../styles/product.css";

const fmtCRC = (n) => (typeof n === "number" ? `‚Ç°${n.toLocaleString("es-CR")}` : n);
const catLabel = (c) => {
    const map = { jewelery: "Joyer√≠a", art: "Pintura", ceramics: "Cer√°mica", textiles: "Textil", decoration: "Decoraci√≥n", bags: "Bolsos", wood: "Madera" };
    return map[c] || (c ? c[0].toUpperCase() + c.slice(1) : "General");
};
const firstImg = (p) => {
    const arr = Array.isArray(p?.images) ? p.images : [];
    const candidate = arr.find(Boolean);
    return candidate ? String(candidate).replace(/\\/g, "/") : null;
};
const isObjectId = (s) => /^[a-f\d]{24}$/i.test(String(s || ""));

export const ProductPage = () => {
    const params = useParams();
    const slugOrId = params.slug || params.id;

    const navigate = useNavigate();
    const location = useLocation();
    const { user, token } = useAuth() || {};
    const authToken = token || localStorage.getItem("token") || localStorage.getItem("authToken");

    const [product, setProduct] = useState(null);
    const [sellerInfo, setSellerInfo] = useState(null);

    const [quantity, setQuantity] = useState(1);
    const [addedToCart, setAddedToCart] = useState(false);

    const [productReviews, setProductReviews] = useState([]);
    const [averageRating, setAverageRating] = useState(0);
    const [userRating, setUserRating] = useState(0);
    const [reviewComment, setReviewComment] = useState("");
    const [showReviewForm, setShowReviewForm] = useState(false);

    const [storeReviews, setStoreReviews] = useState([]);
    const [storeAvg, setStoreAvg] = useState(0);
    const [storeUserRating, setStoreUserRating] = useState(0);
    const [storeReviewComment, setStoreReviewComment] = useState("");
    const [showStoreReviewForm, setShowStoreReviewForm] = useState(false);

    const renderStars = (rating, interactive = false, onStarClick = null) => (
        <div className={`stars ${interactive ? "interactive" : ""}`}>
            {[1, 2, 3, 4, 5].map((star) => (
                <i
                    key={star}
                    className={`fa fa-star ${star <= Math.round(rating) ? "active" : ""}`}
                    onClick={interactive ? () => onStarClick(star) : undefined}
                />
            ))}
        </div>
    );

    const load = async () => {
        try {
            const resDetail = await ShopAPI.get(slugOrId);
            const detail = resDetail?.data?.data;
            if (!detail) { setProduct(null); return; }
            setProduct(detail);

            if (detail.vendorId) {
                const vendorId = detail.vendorId._id || detail.vendorId.id || detail.vendorId;
                setSellerInfo({
                    id: vendorId,
                    businessName: detail.vendorId.companyName || detail.vendorId.name,
                    name: detail.vendorId.name,
                    role: detail.vendorId.role,
                    avatar: detail.vendorId.avatar,
                });
            } else setSellerInfo(null);

            const pid = detail._id || detail.id;
            if (pid) {
                const r = await ReviewsAPI.listProduct(pid, { page: 1, limit: 50 });
                const list = r?.data?.data || [];
                setProductReviews(list);
                const avg = list.reduce((a, b) => a + (b.rating || 0), 0) / (list.length || 1);
                setAverageRating(list.length ? avg : 0);
                const mine = user && list.find((x) => String(x.userId?._id || x.userId) === String(user.id));
                if (mine) { setUserRating(mine.rating); setReviewComment(mine.comment || ""); }
            } else { setProductReviews([]); setAverageRating(0); }

            const vid = detail.vendorId && (detail.vendorId._id || detail.vendorId.id || detail.vendorId);
            if (vid && isObjectId(vid)) {
                const r2 = await ReviewsAPI.listStore(vid, { page: 1, limit: 50 });
                const list2 = r2?.data?.data || [];
                setStoreReviews(list2);
                const avg2 = list2.reduce((a, b) => a + (b.rating || 0), 0) / (list2.length || 1);
                setStoreAvg(list2.length ? avg2 : 0);
                const mine2 = user && list2.find((x) => String(x.userId?._id || x.userId) === String(user.id));
                if (mine2) { setStoreUserRating(mine2.rating); setStoreReviewComment(mine2.comment || ""); }
            } else { setStoreReviews([]); setStoreAvg(0); }
        } catch (err) {
            console.error("Product detail error:", err?.response?.data || err.message);
            setProduct(null);
        }
    };

    useEffect(() => { load(); /* eslint-disable-next-line */ }, [slugOrId]);

    const imgSrc = React.useMemo(() => {
        if (!product) return noImage;
        const raw = Array.isArray(product?.images) && product.images.length
            ? product.images.find(Boolean)
            : product?.image || product?.img || product?.thumbnail || null;
        if (!raw) return noImage;
        const normalized = String(raw).replace(/\\/g, "/");
        try { return resolveImgUrl(normalized) || normalized || noImage; }
        catch { return normalized || noImage; }
    }, [product]);

    // üöÄ Server-first: si no hay sesi√≥n -> pedir login (sin 'location' global)
    const handleAddToCart = async () => {
        if (!product) return;
        const id = product._id || product.id;

        if (!authToken) {
            const next = encodeURIComponent(`${location.pathname}${location.search || ""}`);
            return navigate(`/login?next=${next}`);
        }

        try {
            await CartAPI.add({ productId: id, quantity }, authToken);
            setAddedToCart(true);
            setTimeout(() => setAddedToCart(false), 2000);
        } catch (e) {
            const status = e?.response?.status;
            if (status === 401 || status === 403) {
                const next = encodeURIComponent(`${location.pathname}${location.search || ""}`);
                return navigate(`/login?next=${next}`);
            }
            alert("No se pudo guardar en tu carrito");
        }
    };

    const handleSubmitReview = async () => {
        if (!user || user.userType !== "buyer" || !product || userRating === 0) return;
        try {
            const pid = product._id || product.id;
            await ReviewsAPI.upsertProduct({ productId: pid, rating: userRating, comment: reviewComment }, authToken);
            await load(); setShowReviewForm(false); alert("¬°Rese√±a del producto guardada!");
        } catch (error) {
            console.error("Error al guardar rese√±a:", error);
            alert(error?.response?.data?.error || "Error al enviar la rese√±a");
        }
    };

    const handleSubmitStoreReview = async () => {
        if (!user || user.userType !== "buyer" || !sellerInfo || storeUserRating === 0) return;
        try {
            await ReviewsAPI.upsertStore({ vendorId: sellerInfo.id, rating: storeUserRating, comment: storeReviewComment }, authToken);
            await load(); setShowStoreReviewForm(false); alert("¬°Rese√±a de la tienda guardada!");
        } catch (error) {
            console.error("Error al guardar rese√±a tienda:", error);
            alert(error?.response?.data?.error || "Error al enviar la rese√±a");
        }
    };

    const mergedReviews = productReviews;

    if (!product) {
        return (
            <main className="product-container">
                <div className="product-not-found">
                    <h2>Producto no encontrado</h2>
                    <Link to="/shop" className="btn-back-shop">Volver a la tienda</Link>
                </div>
            </main>
        );
    }

    return (
        <main className="product-container">
            {/* Breadcrumb */}
            <nav className="breadcrumb">
                <Link to="/">Inicio</Link><span>/</span>
                <Link to="/shop">Tienda</Link><span>/</span>
                <span className="current">{product.title}</span>
            </nav>

            <div className="product-detail">
                {/* Imagen */}
                <div className="product-image-section">
                    <div className="product-image-main">
                        <img className="product-image-sim large" src={imgSrc} alt={product.title}
                            loading="lazy" decoding="async" referrerPolicy="no-referrer" crossOrigin="anonymous"
                            onError={(e) => { e.currentTarget.onerror = null; e.currentTarget.src = noImage; }} />
                    </div>
                </div>

                {/* Info */}
                <div className="product-info-section">
                    <div className="product-category">{catLabel(product.category)}</div>
                    <h1 className="product-title">{product.title}</h1>

                    <div className="product-rating">
                        {renderStars(averageRating)}
                        <span className="rating-text">
                            {averageRating > 0 ? averageRating.toFixed(1) : "Sin calificar"}
                            {productReviews.length ? ` (${productReviews.length} rese√±as)` : ""}
                        </span>
                    </div>

                    <div className="product-price-main">{fmtCRC(product.price)}</div>

                    <div className="product-description">
                        <h3>Descripci√≥n</h3>
                        <p>{product.description || "Sin descripci√≥n"}</p>
                    </div>

                    <div className="product-purchase">
                        <div className="quantity-section">
                            <label>Cantidad:</label>
                            <div className="quantity-controls">
                                <button onClick={() => setQuantity(Math.max(1, quantity - 1))} className="quantity-btn"><i className="fa fa-minus" /></button>
                                <span className="quantity-display">{quantity}</span>
                                <button onClick={() => setQuantity(quantity + 1)} className="quantity-btn"><i className="fa fa-plus" /></button>
                            </div>
                        </div>

                        <div className="purchase-actions">
                            <button className={`btn-add-to-cart ${addedToCart ? "added" : ""}`} onClick={handleAddToCart} disabled={product.stock <= 0}>
                                {addedToCart ? (<><i className="fa fa-check" /> ¬°Agregado!</>) : (<><i className="fa fa-shopping-cart" /> Agregar al carrito</>)}
                            </button>
                            <button className="btn-buy-now" disabled={product.stock <= 0} onClick={() => { handleAddToCart(); setTimeout(() => navigate("/cart"), 400); }}>
                                <i className="fa fa-bolt" /> Comprar ahora
                            </button>
                        </div>

                        {user && user.userType === "buyer" && (
                            <button className="btn-review-product" onClick={() => setShowReviewForm(true)}>
                                <i className="fa fa-star" /> Evaluar Producto
                            </button>
                        )}
                    </div>

                    <div className="product-features">
                        <div className="feature"><i className="fa fa-truck" /><span>Env√≠o a todo Costa Rica</span></div>
                        <div className="feature"><i className="fa fa-shield-alt" /><span>Garant√≠a de calidad artesanal</span></div>
                        <div className="feature"><i className="fa fa-heart" /><span>Hecho con amor por artesanos locales</span></div>
                    </div>
                </div>
            </div>

            {/* Vendedor */}
            {sellerInfo && (
                <div className="seller-info-section">
                    <h2>üè™ Informaci√≥n del Vendedor</h2>
                    <div className="seller-card">
                        <div className="seller-header">
                            <div className="seller-avatar">{sellerInfo.avatar ? <img src={sellerInfo.avatar} alt={sellerInfo.businessName || sellerInfo.name} /> : <i className="fa fa-store" />}</div>
                            <div className="seller-details">
                                <h3>{sellerInfo.businessName || sellerInfo.name}</h3>
                                {sellerInfo.role && (<p className="seller-category">{String(sellerInfo.role).toUpperCase()}</p>)}
                            </div>
                        </div>
                        <div className="seller-actions">
                            <Link to={`/seller-profile/${sellerInfo.id || product.vendorId}`} className="btn-view-store"><i className="fa fa-eye" /> Ver Tienda Completa</Link>
                            {user && user.userType === "buyer" && isObjectId(sellerInfo.id) && (
                                <button onClick={() => setShowStoreReviewForm(true)} className="btn-review-product" style={{ marginLeft: 8 }}>
                                    <i className="fa fa-star" /> Evaluar Tienda
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            )}

            {/* Rese√±as de PRODUCTO */}
            <div className="product-reviews-section">
                <h2>‚≠ê Rese√±as del Producto</h2>
                {mergedReviews.length ? (
                    <div className="reviews-list">
                        {mergedReviews.slice(0, 10).map((rv) => (
                            <div key={rv._id || rv.id} className="review-item">
                                <div className="review-header">
                                    <div className="reviewer-info">
                                        {rv.userId?.avatar ? <img src={rv.userId.avatar} alt={rv.userId?.name} className="review-avatar" /> : <i className="fa fa-user-circle" />}
                                        <span>{rv.userId?.name || "Usuario"}</span>
                                    </div>
                                    <div className="review-rating">{renderStars(rv.rating || 0)}</div>
                                </div>
                                <div className="review-date">{new Date(rv.createdAt || rv.date).toLocaleDateString("es-CR")}</div>
                                {rv.comment && <div className="review-comment">{rv.comment}</div>}
                            </div>
                        ))}
                    </div>
                ) : <p className="no-reviews">A√∫n no hay rese√±as para este producto.</p>}
            </div>

            {/* Rese√±as de TIENDA */}
            {sellerInfo && (
                <div className="product-reviews-section">
                    <h2>üè™ Rese√±as de la Tienda</h2>
                    {storeReviews.length ? (
                        <div className="reviews-list">
                            {storeReviews.slice(0, 10).map((rv) => (
                                <div key={rv._id} className="review-item">
                                    <div className="review-header">
                                        <div className="reviewer-info">
                                            {rv.userId?.avatar ? <img src={rv.userId.avatar} alt={rv.userId?.name} className="review-avatar" /> : <i className="fa fa-user-circle" />}
                                            <span>{rv.userId?.name || "Usuario"}</span>
                                        </div>
                                        <div className="review-rating">{renderStars(rv.rating || 0)}</div>
                                    </div>
                                    <div className="review-date">{new Date(rv.createdAt).toLocaleDateString("es-CR")}</div>
                                    {rv.comment && <div className="review-comment">{rv.comment}</div>}
                                </div>
                            ))}
                        </div>
                    ) : <p className="no-reviews">A√∫n no hay rese√±as para esta tienda.</p>}
                </div>
            )}

            <div className="navigation-actions">
                <Link to="/shop" className="btn-back"><i className="fa fa-arrow-left" /> Volver a la tienda</Link>
            </div>

            {/* Modales‚Ä¶ (sin cambios visuales) */}
            {showReviewForm && (
                <div className="modal-overlay" onClick={() => setShowReviewForm(false)}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>Evaluar Producto</h2>
                            <button onClick={() => setShowReviewForm(false)} className="modal-close"><i className="fa fa-times" /></button>
                        </div>
                        <div className="modal-body">
                            <div className="rating-section"><label>Calificaci√≥n del producto:</label>{renderStars(userRating, true, setUserRating)}</div>
                            <div className="comment-section"><label>Comentario (opcional):</label><textarea value={reviewComment} onChange={(e) => setReviewComment(e.target.value)} placeholder="Contanos tu experiencia‚Ä¶" rows={4} /></div>
                        </div>
                        <div className="modal-footer">
                            <button onClick={() => setShowReviewForm(false)} className="btn-cancel">Cancelar</button>
                            <button onClick={handleSubmitReview} className="btn-submit" disabled={userRating === 0}>Enviar Rese√±a</button>
                        </div>
                    </div>
                </div>
            )}

            {showStoreReviewForm && (
                <div className="modal-overlay" onClick={() => setShowStoreReviewForm(false)}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>Evaluar Tienda</h2>
                            <button onClick={() => setShowStoreReviewForm(false)} className="modal-close"><i className="fa fa-times" /></button>
                        </div>
                        <div className="modal-body">
                            <div className="rating-section"><label>Calificaci√≥n de la tienda:</label>{renderStars(storeUserRating, true, setStoreUserRating)}</div>
                            <div className="comment-section"><label>Comentario (opcional):</label><textarea value={storeReviewComment} onChange={(e) => setStoreReviewComment(e.target.value)} placeholder="¬øC√≥mo fue tu experiencia con la tienda?" rows={4} /></div>
                        </div>
                        <div className="modal-footer">
                            <button onClick={() => setShowStoreReviewForm(false)} className="btn-cancel">Cancelar</button>
                            <button onClick={handleSubmitStoreReview} className="btn-submit" disabled={storeUserRating === 0}>Enviar Rese√±a</button>
                        </div>
                    </div>
                </div>
            )}
        </main>
    );
};

export default ProductPage;
