// src/api/cart.service.js
import { api } from "../api";

const auth = (token) => ({ headers: { Authorization: `Bearer ${token}` } });

export const CartAPI = {
    mine: (token) => api.get("/cart/mine", auth(token)),
    add: ({ productId, quantity = 1 }, token) => api.post("/cart/add", { productId, quantity }, auth(token)),
    update: ({ productId, quantity }, token) => api.patch("/cart/item", { productId, quantity }, auth(token)),
    remove: (productId, token) => api.delete("/cart/item", { params: { product: productId }, ...auth(token) }),
    clear: (token) => api.delete("/cart/clear", auth(token)),
};
