import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../modules/auth/AuthContext';
import { SellerPriceManager } from '../SellerPriceManager';
import '../../styles/dashboard.css';
import '../../styles/inventory.css';

export const SellerInventory = () => {
    const { user } = useAuth();
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('all');
    const [sortBy, setSortBy] = useState('name');
    const [isLoading, setIsLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [showPriceManager, setShowPriceManager] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [managingPriceProduct, setManagingPriceProduct] = useState(null);
    const [formData, setFormData] = useState({
        id: '',
        name: '',
        description: '',
        price: '',
        stock: '',
        category: '',
        status: 'active',
        imageUrl: ''
    });

    const categories = [
        { value: 'all', label: 'Todas las categorías' },
        { value: 'jewelery', label: 'Joyería' },
        { value: 'bags', label: 'Bolsos y Accesorios' },
        { value: 'decoration', label: 'Decoración' },
        { value: 'art', label: 'Arte' },
        { value: 'ceramics', label: 'Cerámica' },
        { value: 'textiles', label: 'Textiles' }
    ];

    const loadProducts = useCallback(() => {
        setIsLoading(true);
        setTimeout(() => {
            // Simular carga de productos desde localStorage o API
            const savedProducts = localStorage.getItem(`products_${user?.id}`);
            if (savedProducts) {
                try {
                    const parsedProducts = JSON.parse(savedProducts);
                    setProducts(parsedProducts);
                } catch (error) {
                    console.error('Error al cargar productos:', error);
                    setProducts(getDefaultProducts());
                }
            } else {
                setProducts(getDefaultProducts());
            }
            setIsLoading(false);
        }, 500);
    }, [user?.id]);

    const getDefaultProducts = () => [
        {
            id: 'prod-001',
            name: 'Collar artesanal de semillas',
            description: 'Hermoso collar hecho a mano con semillas naturales locales',
            price: 12000,
            originalPrice: 15000,
            numericPrice: 12000,
            stock: 15,
            category: 'jewelery',
            status: 'active',
            imageUrl: '',
            hasDiscount: true,
            discountPercentage: 20,
            seller: user?.businessName || user?.name || 'Mi Tienda',
            sellerId: user?.id || 'seller-001',
            createdAt: '2024-01-01',
            lastUpdated: new Date().toISOString()
        },
        {
            id: 'prod-002',
            name: 'Bolso tejido a mano',
            description: 'Bolso tradicional tejido con fibras naturales',
            price: 18500,
            originalPrice: 18500,
            numericPrice: 18500,
            stock: 8,
            category: 'bags',
            status: 'active',
            imageUrl: '',
            hasDiscount: false,
            seller: user?.businessName || user?.name || 'Mi Tienda',
            sellerId: user?.id || 'seller-002',
            createdAt: '2024-01-02',
            lastUpdated: new Date().toISOString()
        },
        {
            id: 'prod-003',
            name: 'Vasija cerámica decorativa',
            description: 'Vasija de cerámica pintada a mano con diseños tradicionales',
            price: 13440,
            originalPrice: 15800,
            numericPrice: 13440,
            stock: 5,
            category: 'ceramics',
            status: 'active',
            imageUrl: '',
            hasDiscount: true,
            discountPercentage: 15,
            seller: user?.businessName || user?.name || 'Mi Tienda',
            sellerId: user?.id || 'seller-003',
            createdAt: '2024-01-03',
            lastUpdated: new Date().toISOString()
        },
        {
            id: 'prod-004',
            name: 'Cuadro paisaje artesanal',
            description: 'Pintura al óleo de paisaje costarricense',
            price: 45000,
            originalPrice: 45000,
            numericPrice: 45000,
            stock: 3,
            category: 'art',
            status: 'active',
            imageUrl: '',
            hasDiscount: false,
            seller: user?.businessName || user?.name || 'Mi Tienda',
            sellerId: user?.id || 'seller-004',
            createdAt: '2024-01-04',
            lastUpdated: new Date().toISOString()
        },
        {
            id: 'prod-005',
            name: 'Aretes de madera tallada',
            description: 'Aretes únicos tallados en madera de guanacaste',
            price: 8500,
            originalPrice: 8500,
            numericPrice: 8500,
            stock: 0,
            category: 'jewelery',
            status: 'out_of_stock',
            imageUrl: '',
            hasDiscount: false,
            seller: user?.businessName || user?.name || 'Mi Tienda',
            sellerId: user?.id || 'seller-005',
            createdAt: '2024-01-05',
            lastUpdated: new Date().toISOString()
        }
    ];

    useEffect(() => {
        loadProducts();
    }, [loadProducts]);

    useEffect(() => {
        let filtered = [...products];

        // Filtrar por búsqueda
        if (searchTerm) {
            filtered = filtered.filter(product =>
                product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                product.description.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        // Filtrar por categoría
        if (selectedCategory !== 'all') {
            filtered = filtered.filter(product => product.category === selectedCategory);
        }

        // Ordenar
        filtered.sort((a, b) => {
            switch (sortBy) {
                case 'name':
                    return a.name.localeCompare(b.name);
                case 'price':
                    return a.price - b.price;
                case 'stock':
                    return b.stock - a.stock;
                case 'date':
                    return new Date(b.lastUpdated) - new Date(a.lastUpdated);
                default:
                    return 0;
            }
        });

        setFilteredProducts(filtered);
    }, [products, searchTerm, selectedCategory, sortBy]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSaveProduct = () => {
        const productData = {
            ...formData,
            id: editingProduct ? editingProduct.id : `prod-${Date.now()}`,
            price: parseInt(formData.price),
            numericPrice: parseInt(formData.price),
            originalPrice: parseInt(formData.price),
            stock: parseInt(formData.stock),
            lastUpdated: new Date().toISOString(),
            createdAt: editingProduct ? editingProduct.createdAt : new Date().toISOString(),
            hasDiscount: false,
            discountPercentage: 0,
            seller: user?.businessName || user?.name || 'Mi Tienda',
            sellerId: user?.id || 'seller-unknown'
        };

        let updatedProducts;
        if (editingProduct) {
            updatedProducts = products.map(p => p.id === productData.id ? productData : p);
        } else {
            updatedProducts = [...products, productData];
        }

        setProducts(updatedProducts);
        localStorage.setItem(`products_${user?.id}`, JSON.stringify(updatedProducts));

        setShowModal(false);
        setEditingProduct(null);
        resetForm();
    };

    const handleEditProduct = (product) => {
        setEditingProduct(product);
        setFormData({
            id: product.id,
            name: product.name,
            description: product.description,
            price: product.numericPrice.toString(),
            stock: product.stock.toString(),
            category: product.category,
            status: product.status,
            imageUrl: product.imageUrl || ''
        });
        setShowModal(true);
    };

    const handleManagePrice = (product) => {
        setManagingPriceProduct(product);
        setShowPriceManager(true);
    };

    const handlePriceUpdate = (updatedProduct, priceUpdateData) => {
        // Actualizar el producto en el estado local
        const updatedProducts = products.map(p =>
            p.id === updatedProduct.id ? updatedProduct : p
        );
        setProducts(updatedProducts);
        localStorage.setItem(`products_${user?.id}`, JSON.stringify(updatedProducts));

        // Log para desarrollo
        console.log('Precio actualizado:', {
            product: updatedProduct.name,
            oldPrice: priceUpdateData.oldPrice,
            newPrice: priceUpdateData.newPrice,
            discount: priceUpdateData.hasDiscount ? `${priceUpdateData.discountPercentage}%` : 'Sin descuento'
        });
    };

    const handleDeleteProduct = (productId) => {
        if (window.confirm('¿Estás seguro de que deseas eliminar este producto?')) {
            const updatedProducts = products.filter(p => p.id !== productId);
            setProducts(updatedProducts);
            localStorage.setItem(`products_${user?.id}`, JSON.stringify(updatedProducts));
        }
    };

    const resetForm = () => {
        setFormData({
            id: '',
            name: '',
            description: '',
            price: '',
            stock: '',
            category: '',
            status: 'active',
            imageUrl: ''
        });
    };

    const openAddModal = () => {
        resetForm();
        setEditingProduct(null);
        setShowModal(true);
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'active': return '#4caf50';
            case 'inactive': return '#9e9e9e';
            case 'out_of_stock': return '#f44336';
            default: return '#666';
        }
    };

    const getStatusText = (status) => {
        switch (status) {
            case 'active': return 'Activo';
            case 'inactive': return 'Inactivo';
            case 'out_of_stock': return 'Sin Stock';
            default: return status;
        }
    };

    const getCategoryLabel = (category) => {
        const cat = categories.find(c => c.value === category);
        return cat ? cat.label : category;
    };

    const getDiscountDisplay = (product) => {
        if (product.hasDiscount) {
            return (
                <div className="discount-info">
                    <span className="discount-badge">-{product.discountPercentage}%</span>
                    <span className="original-price">₡{product.originalPrice.toLocaleString()}</span>
                </div>
            );
        }
        return null;
    };

    if (isLoading) {
        return (
            <div className="dashboard-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Cargando inventario...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-container inventory-container">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <Link to="/seller/dashboard" className="back-button">
                        <i className="fa fa-arrow-left"></i>
                        Regresar al Dashboard
                    </Link>
                    <h1>📦 Gestión de Inventario</h1>
                    <p>Administra todos tus productos</p>
                </div>
                <button onClick={openAddModal} className="btn-add-product">
                    <i className="fa fa-plus"></i>
                    Agregar Producto
                </button>
            </div>

            {/* Controles y Filtros */}
            <div className="inventory-controls">
                <div className="search-section">
                    <div className="search-box">
                        <i className="fa fa-search"></i>
                        <input
                            type="text"
                            placeholder="Buscar productos..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                </div>

                <div className="filter-section">
                    <select
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                        className="filter-select"
                    >
                        {categories.map(cat => (
                            <option key={cat.value} value={cat.value}>
                                {cat.label}
                            </option>
                        ))}
                    </select>

                    <select
                        value={sortBy}
                        onChange={(e) => setSortBy(e.target.value)}
                        className="sort-select"
                    >
                        <option value="name">Ordenar por Nombre</option>
                        <option value="price">Ordenar por Precio</option>
                        <option value="stock">Ordenar por Stock</option>
                        <option value="date">Ordenar por Fecha</option>
                    </select>
                </div>
            </div>

            {/* Estadísticas del Inventario */}
            <div className="inventory-stats">
                <div className="stat-card">
                    <div className="stat-icon">
                        <i className="fa fa-boxes"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{products.length}</h3>
                        <p>Total Productos</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon active">
                        <i className="fa fa-check-circle"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{products.filter(p => p.status === 'active').length}</h3>
                        <p>Productos Activos</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon discount">
                        <i className="fa fa-percentage"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{products.filter(p => p.hasDiscount).length}</h3>
                        <p>Con Descuento</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon out-stock">
                        <i className="fa fa-exclamation-triangle"></i>
                    </div>
                    <div className="stat-content">
                        <h3>{products.filter(p => p.stock === 0).length}</h3>
                        <p>Sin Stock</p>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon total-value">
                        <i className="fa fa-dollar-sign"></i>
                    </div>
                    <div className="stat-content">
                        <h3>₡{products.reduce((total, p) => total + (p.numericPrice * p.stock), 0).toLocaleString()}</h3>
                        <p>Valor Inventario</p>
                    </div>
                </div>
            </div>

            {/* Lista de Productos */}
            <div className="section-card products-table-card">
                <div className="section-header">
                    <h2>📋 Lista de Productos</h2>
                    <span className="products-count">{filteredProducts.length} productos</span>
                </div>

                <div className="products-table-container">
                    <div className="products-table">
                        <div className="table-header">
                            <div className="header-cell">Producto</div>
                            <div className="header-cell">Categoría</div>
                            <div className="header-cell">Precio</div>
                            <div className="header-cell">Stock</div>
                            <div className="header-cell">Estado</div>
                            <div className="header-cell">Acciones</div>
                        </div>

                        {filteredProducts.map(product => (
                            <div key={product.id} className="table-row product-row">
                                <div className="table-cell product-info">
                                    <div className="product-image-placeholder">
                                        <i className="fa fa-image"></i>
                                    </div>
                                    <div className="product-details">
                                        <h4>{product.name}</h4>
                                        <p>{product.description.substring(0, 60)}...</p>
                                        {getDiscountDisplay(product)}
                                    </div>
                                </div>

                                <div className="table-cell category">
                                    <span className="category-badge">
                                        {getCategoryLabel(product.category)}
                                    </span>
                                </div>

                                <div className="table-cell price">
                                    <div className="price-display">
                                        <span className="current-price">₡{product.numericPrice.toLocaleString()}</span>
                                        {product.hasDiscount && (
                                            <span className="savings-amount">
                                                Ahorro: ₡{(product.originalPrice - product.numericPrice).toLocaleString()}
                                            </span>
                                        )}
                                    </div>
                                </div>

                                <div className="table-cell stock">
                                    <span className={`stock-badge ${product.stock === 0 ? 'no-stock' : product.stock <= 5 ? 'low-stock' : 'good-stock'}`}>
                                        {product.stock} unidades
                                    </span>
                                </div>

                                <div className="table-cell status">
                                    <span
                                        className="status-badge"
                                        style={{ backgroundColor: getStatusColor(product.status) }}
                                    >
                                        {getStatusText(product.status)}
                                    </span>
                                </div>

                                <div className="table-cell actions">
                                    <button
                                        onClick={() => handleManagePrice(product)}
                                        className="btn-action price"
                                        title="Gestionar Precio"
                                    >
                                        <i className="fa fa-tag"></i>
                                    </button>
                                    <button
                                        onClick={() => handleEditProduct(product)}
                                        className="btn-action edit"
                                        title="Editar"
                                    >
                                        <i className="fa fa-edit"></i>
                                    </button>
                                    <button
                                        onClick={() => handleDeleteProduct(product.id)}
                                        className="btn-action delete"
                                        title="Eliminar"
                                    >
                                        <i className="fa fa-trash"></i>
                                    </button>
                                </div>
                            </div>
                        ))}

                        {filteredProducts.length === 0 && (
                            <div className="empty-state">
                                <i className="fa fa-box-open"></i>
                                <h3>No hay productos</h3>
                                <p>No se encontraron productos con los filtros aplicados</p>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Modal para Agregar/Editar Producto */}
            {showModal && (
                <div className="modal-overlay" onClick={() => setShowModal(false)}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>{editingProduct ? 'Editar Producto' : 'Agregar Nuevo Producto'}</h2>
                            <button onClick={() => setShowModal(false)} className="modal-close">
                                <i className="fa fa-times"></i>
                            </button>
                        </div>

                        <div className="modal-body">
                            <div className="form-grid">
                                <div className="form-group">
                                    <label>Nombre del Producto</label>
                                    <input
                                        type="text"
                                        name="name"
                                        value={formData.name}
                                        onChange={handleInputChange}
                                        placeholder="Ingresa el nombre del producto"
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Categoría</label>
                                    <select
                                        name="category"
                                        value={formData.category}
                                        onChange={handleInputChange}
                                        required
                                    >
                                        <option value="">Selecciona una categoría</option>
                                        {categories.slice(1).map(cat => (
                                            <option key={cat.value} value={cat.value}>
                                                {cat.label}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>Precio (₡)</label>
                                    <input
                                        type="number"
                                        name="price"
                                        value={formData.price}
                                        onChange={handleInputChange}
                                        placeholder="0"
                                        min="0"
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Stock</label>
                                    <input
                                        type="number"
                                        name="stock"
                                        value={formData.stock}
                                        onChange={handleInputChange}
                                        placeholder="0"
                                        min="0"
                                        required
                                    />
                                </div>

                                <div className="form-group full-width">
                                    <label>Descripción</label>
                                    <textarea
                                        name="description"
                                        value={formData.description}
                                        onChange={handleInputChange}
                                        placeholder="Describe tu producto..."
                                        rows="3"
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Estado</label>
                                    <select
                                        name="status"
                                        value={formData.status}
                                        onChange={handleInputChange}
                                    >
                                        <option value="active">Activo</option>
                                        <option value="inactive">Inactivo</option>
                                        <option value="out_of_stock">Sin Stock</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>URL de Imagen (opcional)</label>
                                    <input
                                        type="url"
                                        name="imageUrl"
                                        value={formData.imageUrl}
                                        onChange={handleInputChange}
                                        placeholder="https://ejemplo.com/imagen.jpg"
                                    />
                                </div>
                            </div>
                        </div>

                        <div className="modal-footer">
                            <button onClick={() => setShowModal(false)} className="btn-cancel">
                                Cancelar
                            </button>
                            <button onClick={handleSaveProduct} className="btn-save">
                                <i className="fa fa-save"></i>
                                {editingProduct ? 'Actualizar' : 'Guardar'} Producto
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Modal de Gestión de Precios */}
            {showPriceManager && managingPriceProduct && (
                <SellerPriceManager
                    product={managingPriceProduct}
                    onPriceUpdate={handlePriceUpdate}
                    onClose={() => {
                        setShowPriceManager(false);
                        setManagingPriceProduct(null);
                    }}
                />
            )}

            {/* Estilos adicionales para los nuevos elementos */}

        </div>
    );
};
