// src/api/shop.service.js
import { api } from "../api";

/** Endpoints públicos (no requieren token) */
export const ShopAPI = {
    list: (params = {}) =>
        api.get("/public/products", { params }).then((r) => r.data),

    detail: (slug) =>
        api.get(`/public/products/${slug}`).then((r) => r.data),

    reviews: (productId, params = {}) =>
        api.get(`/public/reviews`, { params: { product: productId, ...params } }).then((r) => r.data),
};
