// src/pages/ShopPage.js
import React, { useState, useEffect, useMemo } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../modules/auth/AuthContext";
import { CartAPI } from "../api/cart.service";
import "../styles/shop.css";
import { ShopAPI } from "../api/shop.service";
import { resolveImgUrl } from "../utils/resolveImgUrl";
import noImage from "../assets/images/noimage.png";

// ... (helpers tal como los tenés arriba)
const firstImg = (p) => { const c = Array.isArray(p?.images) && p.images.length ? p.images.find(Boolean) : p?.image || p?.img || p?.thumbnail || null; return c ? String(c).replace(/\\/g, "/") : null; };
const normalizeCategory = (cat) => { const c = String(cat || "").toLowerCase(); const map = { jewelery: "joyeria", jewelry: "joyeria", art: "pintura", paintings: "pintura", ceramics: "ceramica", ceramic: "ceramica", textiles: "textil", textile: "textil", decoration: "decoracion", decor: "decoracion", bags: "bolsos", bag: "bolsos", wood: "escultura", sculpture: "escultura", esculturas: "escultura" }; return map[c] || c || "otros"; };
const categoryLabel = (id) => { const map = { todos: "Todos los productos", textil: "Textil", joyeria: "Joyería", pintura: "Pintura", ceramica: "Cerámica", escultura: "Escultura", bolsos: "Bolsos", decoracion: "Decoración", otros: "Otros" }; return map[id] || (id ? id[0].toUpperCase() + id.slice(1) : "Otros"); };
const categoryIcon = (id) => { const map = { textil: "🧵", joyeria: "💎", pintura: "🎨", ceramica: "🏺", escultura: "🗿", bolsos: "👜", decoracion: "🖼️", otros: "🪄" }; return map[id] || "🎨"; };
const fmtCRC = (n) => typeof n === "number" ? `₡${n.toLocaleString("es-CR")}` : String(n || "");
const getNumericPrice = (p) => { if (typeof p?.numericPrice === "number") return p.numericPrice; if (typeof p?.price === "number") return p.price; if (typeof p?.price === "string") { const digits = p.price.replace(/[^\d]/g, ""); return Number(digits || 0); } return 0; };

const mockProducts = [/* (tu mock igual que antes) */];

export const ShopPage = () => {
    const { user, token } = useAuth() || {};
    const authToken = token || localStorage.getItem("token") || localStorage.getItem("authToken");
    const navigate = useNavigate();
    const location = useLocation();

    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState("todos");
    const [searchTerm, setSearchTerm] = useState("");
    const [cartItems, setCartItems] = useState([]);
    const [addingToCart, setAddingToCart] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        try {
            const saved = localStorage.getItem("artesjac-cart");
            if (saved && saved !== "null" && saved !== "[]") setCartItems(JSON.parse(saved));
        } catch { }
    }, []);

    useEffect(() => {
        (async () => {
            setError("");
            try {
                const res = await ShopAPI.list({ limit: 200 });
                const list = Array.isArray(res?.data?.data) ? res.data.data : [];
                const normalized = list.map((p) => ({
                    ...p,
                    _normalizedCategory: normalizeCategory(p.category),
                    _displayName: p.title || p.name || "Producto",
                    _displayPrice: fmtCRC(typeof p.price === "number" ? p.price : p.price),
                }));
                setProducts(normalized);
            } catch (e) {
                setError("No se pudieron cargar los productos. Mostrando datos de ejemplo.");
                const normalizedMock = mockProducts.map((p) => ({
                    ...p,
                    _normalizedCategory: normalizeCategory(p.category),
                    _displayName: p.name,
                    _displayPrice: p.price,
                }));
                setProducts(normalizedMock);
            }
        })();
    }, []);

    useEffect(() => {
        let list = products;
        if (selectedCategory !== "todos") list = list.filter((p) => p._normalizedCategory === selectedCategory);
        if (searchTerm) {
            const s = searchTerm.toLowerCase();
            list = list.filter((p) => (p._displayName || "").toLowerCase().includes(s) || (p.description || "").toLowerCase().includes(s));
        }
        setFilteredProducts(list);
    }, [selectedCategory, searchTerm, products]);

    const categories = useMemo(() => {
        const counts = { textil: 0, joyeria: 0, pintura: 0, ceramica: 0, escultura: 0, bolsos: 0, decoracion: 0, otros: 0 };
        products.forEach((p) => { const key = p._normalizedCategory || "otros"; if (counts[key] !== undefined) counts[key] += 1; else counts.otros += 1; });
        const base = ["todos", "textil", "joyeria", "pintura", "ceramica", "escultura", "bolsos", "decoracion", "otros"];
        return base.map((id) => ({ id, name: categoryLabel(id), count: id === "todos" ? products.length : counts[id], icon: categoryIcon(id) }));
    }, [products]);

    const getImgSrc = (p) => { const c = firstImg(p); if (!c) return noImage; try { return resolveImgUrl(c) || noImage; } catch { return noImage; } };

    // 🚀 Server-first
    const addToCart = async (product) => {
        const itemId = product._id || product.id || product.slug;
        setAddingToCart(itemId);

        if (!authToken) {
            const next = encodeURIComponent(`${location.pathname}${location.search || ""}`);
            setAddingToCart(null);
            return navigate(`/login?next=${next}`);
        }

        try {
            await CartAPI.add({ productId: itemId, quantity: 1 }, authToken);
            // Opcional: mantener indicador local para el banner superior
            const saved = localStorage.getItem("artesjac-cart");
            let current = saved && saved !== "null" ? JSON.parse(saved) : [];
            const idx = current.findIndex((it) => it.id === itemId);
            if (idx > -1) current[idx].quantity = (current[idx].quantity || 1) + 1;
            else current.push({ id: itemId, quantity: 1, numericPrice: getNumericPrice(product), name: product._displayName, category: product._normalizedCategory });
            localStorage.setItem("artesjac-cart", JSON.stringify(current));
            setCartItems(current);

            // mini notificación (sin cambiar tu UI)
            await new Promise((r) => setTimeout(r, 500));
            const n = document.createElement("div");
            n.style.cssText = `position:fixed;top:20px;right:20px;background:linear-gradient(135deg,#4caf50,#45a049);color:#fff;padding:1rem 1.5rem;border-radius:8px;box-shadow:0 4px 15px rgba(76,175,80,.3);z-index:1000;font-weight:bold;animation:slideIn .3s ease;`;
            n.innerHTML = `✅ ${product._displayName} agregado al carrito`;
            document.body.appendChild(n); setTimeout(() => n.remove(), 3000);
        } catch (e) {
            alert("No se pudo agregar al carrito");
        } finally {
            setAddingToCart(null);
        }
    };

    const calculateCartTotal = () => cartItems.reduce((t, it) => t + Number(it.numericPrice || 0) * Number(it.quantity || 0), 0);
    const getTotalCartItems = () => cartItems.reduce((t, it) => t + Number(it.quantity || 0), 0);

    return (
        <main className="shop-container">
            {/* …todo tu markup original (sin cambios visuales)… */}
            {/* Solo cambiamos la lógica de addToCart (arriba) */}
            {/* El resto de tu archivo se mantiene igual */}
            {/* --- Te dejo el grid con el botón usando addToCart(product) --- */}

            <div className="shop-header">
                <h1>Tienda ArtesJAC</h1>
                <p>Descubre arte auténtico costarricense hecho a mano por talentosos artesanos locales</p>
            </div>

            <div className="cart-indicator">
                <div className="cart-indicator-info">
                    <i className="fa fa-shopping-cart"></i>
                    <span>
                        {getTotalCartItems()} producto{getTotalCartItems() !== 1 ? "s" : ""} en el carrito - Total: {fmtCRC(calculateCartTotal())}
                    </span>
                </div>
                <Link to="/cart" className="view-cart-btn">
                    <i className="fa fa-arrow-right"></i> Ver Carrito
                </Link>
            </div>

            {/* …buscador, sidebar, etc… */}

            <section className="products-section">
                {/* header y mensajes… */}
                <div className="products-grid">
                    {filteredProducts.map((p) => {
                        const key = p.slug || p._id || p.id;
                        const href = p.slug ? `/p/${p.slug}` : `/product/${p.id}`;
                        const name = p._displayName;
                        const price = fmtCRC(typeof p.price === "number" ? p.price : p.price);
                        return (
                            <div key={key} className="product-card">
                                <div className="product-media">
                                    <img
                                        src={getImgSrc(p)} alt={`Imagen de ${name}`} className="product-thumb"
                                        loading="lazy" decoding="async" referrerPolicy="no-referrer" crossOrigin="anonymous"
                                        onError={(e) => { if (e.currentTarget.dataset.fallback === "1") return; e.currentTarget.dataset.fallback = "1"; e.currentTarget.src = noImage; }}
                                    />
                                </div>
                                <div className="product-info">
                                    <h3 className="product-name">{name}</h3>
                                    <p className="product-price">{price}</p>
                                    <p className="product-category">{categoryIcon(p._normalizedCategory)} {categoryLabel(p._normalizedCategory)}</p>
                                </div>
                                <div className="product-actions">
                                    <button className="btn-add-to-cart" onClick={() => addToCart(p)} disabled={addingToCart === (p._id || p.id || p.slug)}>
                                        {addingToCart === (p._id || p.id || p.slug) ? (<><i className="fa fa-spinner fa-spin"></i> Agregando...</>) : (<><i className="fa fa-shopping-cart"></i> Agregar al carrito</>)}
                                    </button>
                                    <Link to={href} className="btn-view-details" style={{ textDecoration: "none" }}>
                                        <i className="fa fa-eye"></i> Ver detalles
                                    </Link>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </section>

            <style jsx>{`
        @keyframes slideIn { from { transform: translateX(100%); opacity: 0; } to { transform: translateX(0); opacity: 1; } }
      `}</style>
        </main>
    );
};
