// src/pages/ShopPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { Link } from 'react-router-dom';
import '../styles/shop.css';
import { ShopAPI } from '../api/shop.service';
import { resolveImgUrl } from "../utils/resolveImgUrl";
import noImage from "../assets/images/noimage.png";

// Devuelve la primera imagen válida o null
const firstImg = (p) => {
    const arr = Array.isArray(p?.images) ? p.images : [];
    const candidate = arr.find(Boolean);
    return candidate ? String(candidate).replace(/\\/g, "/") : null;
};

// Formatea colones
const fmtCRC = (n) => typeof n === "number"
    ? `₡${n.toLocaleString('es-CR')}`
    : n;

export const ShopPage = () => {
    const [items, setItems] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [q, setQ] = useState("");
    const [category, setCategory] = useState("");
    const [sort, setSort] = useState("new");
    const [page, setPage] = useState(1);
    const [pages, setPages] = useState(1);

    const fetchList = async () => {
        setIsLoading(true);
        try {
            const { data, pagination } = await ShopAPI.list({ q, category, sort, page, limit: 12 });
            setItems(Array.isArray(data) ? data : []);
            setPages(pagination?.pages || 1);
        } catch (e) {
            console.error("Shop list error:", e?.response?.data || e.message);
            setItems([]);
            setPages(1);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => { fetchList(); /* load on mount */ }, []);
    useEffect(() => { fetchList(); /* reload on filters */ }, [q, category, sort, page]);

    const categories = useMemo(() => ([
        { value: "", label: "Todas" },
        { value: "jewelery", label: "Joyería" },
        { value: "art", label: "Pintura" },
        { value: "ceramics", label: "Cerámica" },
        { value: "textiles", label: "Textil" },
        { value: "decoration", label: "Decoración" },
        { value: "bags", label: "Bolsos" },
        { value: "wood", label: "Madera" },
    ]), []);

    return (
        <div className="shop-page">
            <div className="shop-header">
                <h1>Tienda</h1>

                <div className="shop-filters">
                    <input
                        type="text"
                        placeholder="Buscar productos…"
                        value={q}
                        onChange={(e) => { setPage(1); setQ(e.target.value); }}
                    />

                    <select value={category} onChange={(e) => { setPage(1); setCategory(e.target.value); }}>
                        {categories.map(c => <option key={c.value} value={c.value}>{c.label}</option>)}
                    </select>

                    <select value={sort} onChange={(e) => { setPage(1); setSort(e.target.value); }}>
                        <option value="new">Más recientes</option>
                        <option value="old">Más antiguos</option>
                        <option value="price_asc">Precio: menor a mayor</option>
                        <option value="price_desc">Precio: mayor a menor</option>
                    </select>
                </div>
            </div>

            {isLoading ? (
                <p>Cargando productos…</p>
            ) : items.length === 0 ? (
                <p>No hay productos.</p>
            ) : (
                <div className="shop-grid">
                    {items.map(p => {
                        const img = firstImg(p);
                        return (
                            <div key={p.slug} className="shop-card">
                                <Link to={`/p/${p.slug}`} className="img-wrap">
                                    <img
                                        src={img ? resolveImgUrl(img) : noImage}
                                        alt={p.title}
                                        onError={(e) => { e.currentTarget.src = noImage; }}
                                    />
                                </Link>
                                <div className="shop-card-body">
                                    <h3 title={p.title}>
                                        <Link to={`/p/${p.slug}`}>{p.title}</Link>
                                    </h3>
                                    <div className="price">{fmtCRC(p.price)}</div>
                                    {p.stock <= 0 && <span className="badge badge-out">Sin stock</span>}
                                </div>
                            </div>
                        );
                    })}
                </div>
            )}

            {pages > 1 && (
                <div className="pagination">
                    <button disabled={page <= 1} onClick={() => setPage(page - 1)}>Anterior</button>
                    <span>{page} / {pages}</span>
                    <button disabled={page >= pages} onClick={() => setPage(page + 1)}>Siguiente</button>
                </div>
            )}
        </div>
    );
};

export default ShopPage;
